import React from 'react';
import s from './style.module.css';
import cn from 'classnames';
import { ReactComponent as Cross } from '../../assets/image/ic-close-input.svg';

const Modal = ({ active, setActive, children }) => {
  return (
    <div className={cn(s.modal, { [s.active]: active })} onClick={() => setActive(false)}>
      <div className={cn(s.content, { [s.active]: active })} onClick={(e) => e.stopPropagation()}>
        <Cross className={s.cross} onClick={() => setActive(false)}></Cross>
        {children}
      </div>
    </div>
  );
};

export default Modal;
