import React, { useState, useEffect, useCallback } from 'react';
import { Spin } from 'antd';
import { Routes, Route } from 'react-router-dom';
import Header from '../Header/Header';
import Logo from '../Logo/Logo';
import Search from '../Search/Search';
import CardList from '../../pages/Catalog/CardList';
import api from '../../utils/api';
import isLike from '../../utils/utils';

import s from './style.module.css';
import ProductPage from '../../pages/Product/ProductPage';
import FAQPage from '../../pages/FAQ/FAQPage';
import Footer from '../Footer/Footer';
import NotFoundPage from '../../pages/NotFound/NotFound';
import { UserContext } from '../../context/userContext';
import { LoadingContext } from '../../context/loadingContext';
import { theme, ThemeContext } from '../../context/themeContext';

function App() {
  const [cards, setCards] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentTheme, setTheme] = useState(theme.light);
  const [user, setUser] = useState({});

  const handleRequest = useCallback((newSearchQuery) => {
    setIsLoading(true);
    api
      .search(newSearchQuery)
      .then((data) => setCards(data))
      .finally(() => setIsLoading(false));
  }, []);

  const handleUpdateUser = useCallback((updateUser) => {
    api.updateUserInfo(updateUser).then((newUser) => setUser(newUser));
  }, []);

  useEffect(() => {
    Promise.all([api.getUserInfo(), api.getAllProducts()]).then(([userData, productsData]) => {
      setUser(userData);
      setCards(productsData.products);
      setIsLoading(false);
    });
  }, []);

  const handleProductLike = useCallback(
    (productId, productLikes) => {
      const isLiked = isLike(productLikes, user._id);
      return api.changeLikeStatus(productId, isLiked).then((updateProduct) => {
        const updateCards = cards.map((card) => (card._id === updateProduct._id ? updateProduct : card));
        setCards(updateCards);
        return updateProduct;
      });
    },
    [cards, user._id]
  );

  const changeIsLoading = (isLoading) => {
    setIsLoading(isLoading);
  };

  const toggleTheme = () => {
    console.log('change theme');
    currentTheme === theme.light ? setTheme(theme.dark) : setTheme(theme.light);
  };

  return (
    <ThemeContext.Provider value={{ theme: currentTheme, toggleTheme }}>
      <LoadingContext.Provider value={{ isLoading, changeIsLoading }}>
        <UserContext.Provider value={{ user, handleProductLike }}>
          <Header user={user} handleUpdateUser={handleUpdateUser}>
            <Logo />
            <Search onSearch={handleRequest} />
          </Header>
          <Routes>
            <Route path="/" element={<CardList cards={cards}></CardList>}></Route>
            <Route path="/product/:productId" element={<ProductPage></ProductPage>}></Route>
            <Route path="/faq" element={<FAQPage></FAQPage>}></Route>
            <Route path="*" element={<NotFoundPage></NotFoundPage>}></Route>
          </Routes>
          {/* <div className={s.container}>{isLoading ? <Spin></Spin> : <FAQPage />}</div> */}
          <Footer></Footer>
        </UserContext.Provider>
      </LoadingContext.Provider>
    </ThemeContext.Provider>
  );
}

export default App;
