import React from 'react';
import s from './style.module.css';
import { useForm } from 'react-hook-form';
import FormInput from './FormInput';
import Button from '../Button/Button';

const RegistrationForm = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({ mode: 'onBlur' });

  const emailRegexp =
    /(?:[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*|"(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21\x23-\x5b\x5d-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])*")@(?:(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?|\[(?:(?:(2(5[0-5]|[0-4][0-9])|1[0-9][0-9]|[1-9]?[0-9]))\.){3}(?:(2(5[0-5]|[0-4][0-9])|1[0-9][0-9]|[1-9]?[0-9])|[a-z0-9-]*[a-z0-9]:(?:[\x01-\x08\x0b\x0c\x0e-\x1f\x21-\x5a\x53-\x7f]|\\[\x01-\x09\x0b\x0c\x0e-\x7f])+)\])/;

  const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/;

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <form className={s.form} onSubmit={handleSubmit(onSubmit)}>
      <h2 className={s.title}>Регистрация</h2>
      <FormInput
        {...register('email', { required: true, pattern: { value: emailRegexp, message: 'Введите корректную почту по образцу email@email.com' } })}
        type="text"
        placeholder="Email"
        autoComplete="off"
      />
      {errors?.email && <div className={s.error}>{errors.email.message}</div>}
      <FormInput
        {...register('password', { required: true, pattern: { value: passwordRegex, message: 'Пароль должен содержать 8 символов' } })}
        type="password"
        placeholder="Password"
        autoComplete="new-password"
      />
      {errors?.password && <div className={s.error}>{errors.password.message}</div>}
      <div className={s.info}>Регистрируясь на сайте, вы соглашаетесь с нашими Правилами и Политикой конфиденциальности и соглашаетесь на информационную рассылку.</div>
      <Button>Зарегистрироваться</Button>
      <Button type="secondary">Войти</Button>
    </form>
  );
};

export default RegistrationForm;
