const onResponce = (res) => (res.ok ? res.json() : Promise.reject(`Error: ${res.status}`));

class Api {
  constructor({ url, token }) {
    this.url = url;
    this.token = `Bearer ${token}`;
  }

  getAllProducts() {
    return fetch(`${this.url}/products`, {
      headers: {
        Authorization: this.token,
      },
    }).then(onResponce);
  }

  getUserInfo() {
    return fetch(`${this.url}/users/me`, {
      headers: {
        Authorization: this.token,
      },
    }).then(onResponce);
  }

  updateUserInfo(updateUser) {
    return fetch(`${this.url}/users/me`, {
      method: 'PATCH',
      headers: {
        'Content-type': 'application/json',
        Authorization: this.token,
      },
      body: JSON.stringify(updateUser),
    }).then(onResponce);
  }

  search(serachQuery) {
    return fetch(`${this.url}/products/search?query=${serachQuery}`, {
      headers: {
        Authorization: this.token,
      },
    }).then(onResponce);
  }

  changeLikeStatus(productId, isLike) {
    return fetch(`${this.url}/products/likes/${productId}`, {
      method: isLike ? 'DELETE' : 'PUT',
      headers: {
        'Content-type': 'application/json',
        Authorization: this.token,
      },
    }).then(onResponce);
  }
}

